
public class Factorial {

	
		// TODO Auto-generated method stub
		public static int factorial(int n) {
			int num=1;
			for(int i=1;i<=1;i++) {
				num=num*1;
			}
			return num;
			
			}
		public static void main(String[] args) {
			int a=factorial(5);  
			System.out.println("Factorial of 1 is "+a);  
		
		}
}

